import React, { Component } from 'react'

export class Intro extends Component {
    render() {
        return (
            <div class="Intro">
                <h1>Welcome to Loan Management System</h1>
                <h4>Click on the 'Register' button to create an account</h4>
                <h3>OR</h3>
                <h4>Click on the 'Login' button to login with an existing account</h4>
            </div>
        )
    }
}

export default Intro
